import './Auth.css';
import React, {useRef, useState, useEffect} from "react";
import { useNavigate } from 'react-router-dom';
import domain from '../../config'
import axios from "axios";

function Auth() {
    const [result, setResult] = useState([])
    const [token, setToken] = useState(null)

    const navigate = useNavigate()

    const fetchData = async(token) => {
        try{
            const {data} = await axios.post(`${domain}api/check_token/`, {
                "TOKEN": token
            })
            setResult(data)

        } catch(error) {
            console.log(error)
        }
        console.log(result)
        if (result === true){
            localStorage.setItem("token", token)
            navigate('/home')
        }

    }

    return (
        <div>
            <div className='menuPart'>
                <div className='logoText'>Финансовая алхимия</div>

                <div className='lowText'>Авторизация</div>
                <input className='depositInput' value={token} onChange={event => setToken(event.target.value)} type="num" id="message" name="message" />

                <button className='normalText' onClick={fetchData(token)}>Войти</button>
            </div>
        </div>
    );
}

export default Auth;
