import './Auth.css';
import React, {useState, useEffect} from "react";
import { useNavigate } from 'react-router-dom';
import btnIcon from "../../svg/authButton.png"
import domain from '../../config'
import axios from "axios";

function Auth() {
    const [token, setToken] = useState()

    const navigate = useNavigate()

useEffect((fetchData, token) => {
    const keyDownHandler = event => {
        if (event.key === 'Enter') {
        event.preventDefault();
        fetchData(token);
        }
    }
    document.addEventListener('keydown', keyDownHandler);
    return () => {
        document.removeEventListener('keydown', keyDownHandler);
        };
    }, []);
    const fetchData = async(token) => {
        try{
            const {data} = await axios.post(`${domain}api/check_token/`, {
                "TOKEN": token
            })
            console.log(data)
            if (data === true){
                localStorage.setItem("token", token)
                navigate('/home')
            }

        } catch(error) {
            console.log(error)
        }

    }

    return (
        <div className='authPage'>
            <div className='logoBar'>
                <div className='logo'>Ф</div>
                <div className='logoBoxText'>финансовая <br/>алхимия</div>
            </div>
            <div className='tokenBox'>
                <div className='inputTyping'>
                    <input className='tokenInput' placeholder='Введите ваш токен.'  value={token} onChange={event => setToken(event.target.value)} type="num" id="message" name="message" />
                    <button className='authBtn' onClick={() => fetchData(token)}>
                        <img src={btnIcon} alt="" />
                    </button>
                </div>
                <div className='inputBox'></div>
            </div>
        </div>
    );
}

export default Auth;
