import './Auth.css';
import React, {useState} from "react";
import { useNavigate } from 'react-router-dom';
import domain from '../../config'
import axios from "axios";

function Auth() {
    const [token, setToken] = useState()

    const navigate = useNavigate()

    const fetchData = async(token) => {
        try{
            const {data} = await axios.post(`${domain}api/check_token/`, {
                "TOKEN": token
            })
            console.log(data)
            if (data === true){
                localStorage.setItem("token", token)
                navigate('/home')
            }

        } catch(error) {
            console.log(error)
        }

    }

    return (
        <div className='authPage'>
            <div className='logoBar'>
                <div className='logo'>Ф</div>
            </div>
            <input className='depositInput' value={token} onChange={event => setToken(event.target.value)} type="num" id="message" name="message" />
            <button className='normalText' onClick={() => fetchData(token)}>Войти</button>
        </div>
    );
}

export default Auth;
